mapper.select:
select pdr.id, pdr.older_id as olderId, pdr.older_name as olderName, pdr.applicant_name as applicantName, pdr.phone_no as phoneNo, pr.relationShip, pdr.audit_flag as auditFlag, pdr.audit_result as auditResult, pdo.valid_flag as validFlag, pdd.delivery_name as deliveryName, pdr.audit_name as auditName, pdr.audit_time as auditTime, pdr.send_flag as sendFlag, pdr.note from pension_drugreceive_record pdr, pension_dispense_older pdo, pension_dic_delivery pdd, pension_dic_relationShip pr where pdo.older_id=pdr.older_id and pdo.delivery_id=pdd.id and pdr.cleared=2 and pdo.cleared=2 and pr.id=pdr.relationship_id
and pdr.older_id=#{olderId,jdbcType=INTEGER}
and pdr.audit_flag=#{isAudit,jdbcType=VARCHAR}
and pdr.audit_result=#{auditResult,jdbcType=VARCHAR}
and pdr.id=#{recordId,jdbcType=VARCHAR}
and pdd.id=#{deliery,jdbcType=INTEGER}

select pdr.id, pdr.older_id as olderId, pdr.older_name as olderName, pdr.applicant_name as applicantName, pdr.phone_no as phoneNo, pr.relationShip, pd.drugreceive_name as drugreceiveName, pdd.total_amount as totalAmount, pdd.single_dose as singleDose, pdd.unit, pdd.shelflife_time as shelflifeTime, if(pdd.use_flag=1,true,false) as useFlag, if(pdd.valid_flag=1,true,false) as validF from pension_drugreceive_record pdr, pension_drugreceive_detail pdd, pension_dic_drugreceive pd, pension_dic_relationship pr where pdr.id=pdd.record_id and pdd.drugreceive_id=pd.id and pr.id=pdr.relationship_id and pdr.audit_result=1 and pdd.shelflife_time <= #{noticeDate,jdbcType=TIMESTAMP}
and pdr.older_id=#{olderId,jdbcType=INTEGER}
and pd.drugreceive_name=#{drugName,jdbcType=VARCHAR}

select pdr.id, pdr.older_id as olderId, pdr.older_name as olderName, pdr.applicant_name as applicantName, pdr.phone_no as phoneNo, pr.relationShip, pd.drugreceive_name as drugreceiveName, pdd.total_amount as totalAmount, pdd.single_dose as singleDose, pdd.unit, pdd.shelflife_time as shelflifeTime, pdd.use_flag as useFlag, pdd.valid_flag as validFlag, if(pdd.valid_flag=1,true,false) as validF, if(pdd.morning_flag=1,true,false) as morningFlag, if(pdd.noon_flag=1,true,false) as noonFlag, if(pdd.night_flag=1,true,false) as nightFlag from pension_drugreceive_record pdr, pension_drugreceive_detail pdd, pension_dic_drugreceive pd, pension_dic_relationship pr where pdr.id=pdd.record_id and pdd.drugreceive_id=pd.id and pr.id=pdr.relationship_id and pdr.audit_result=1 and pdd.use_flag=1
and pd.drugreceive_name=#{drugName,jdbcType=VARCHAR}
and pdr.older_id=#{olderId,jdbcType=INTEGER}

select pr.older_id as olderId, pr.older_name as olderName, sum(pd.total_amount) as totalAmount, p.drugreceive_name as drugreceiveName, p.id as drugreceiveId from pension_drugreceive_record pr, pension_drugreceive_detail pd, pension_dic_drugreceive p where pr.audit_result=1 and pd.record_id=pr.id and p.id=pd.drugreceive_id
and p.drugreceive_name=#{drugName,jdbcType=VARCHAR}
and pr.older_id=#{olderId,jdbcType=INTEGER}
group by pr.older_id,pr.older_name,p.drugreceive_name
