mapper.select:
select pia.id, pia.older_id as olderId, pia.older_name as olderName, pia.older_age as olderAge, pia.treatment_scheme as treatmentScheme, pia.doctor_name as doctorName, pia.infusion_flag as infusionFlag, pia.hospitalproof_flag as hospitalproofFlag, pia.hospital_seal as hospitalSeal, pia.start_time as startTime, pia.end_time as endTime, pia.last_time as lastTime, pia.count_numbers as countNumbers, pia.execute_plan as executePlan, pia.execute_numbers as executeNumbers, pia.verify_flag as verifyFlag, pia.verify_result as verifyResult, pia.verify_time as verifyTime, pia.verifier_name as verifierName, pia.generate_flag as generateFlag, pia.apply_name as applyName, pia.send_flag as sendFlag, pia.note from pension_infusion_apply pia where pia.cleared=2
and pia.older_id=#{olderId,jdbcType=INTEGER}
and pia.verify_flag=#{verifyFlag,jdbcType=VARCHAR}
and pia.verify_result=#{verifyResult,jdbcType=VARCHAR}
and pia.id=#{applyId,jdbcType=VARCHAR}
and unix_timestamp(pia.start_time) >=unix_timestamp(#{startDate})
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >unix_timestamp(pia.start_time)
order by pia.generate_flag desc,pia.start_time desc

select pia.id, pia.older_id as olderId, pia.older_name as olderName, pia.older_age as olderAge, pia.treatment_scheme as treatmentScheme, pia.doctor_name as doctorName, pia.infusion_flag as infusionFlag, pia.hospitalproof_flag as hospitalproofFlag, pia.hospital_seal as hospitalSeal, pia.start_time as startTime, pia.end_time as endTime, pia.last_time as lastTime, pia.count_numbers as countNumbers, pia.execute_plan as executePlan, pia.execute_numbers as executeNumbers, pia.verify_flag as verifyFlag, pia.verify_result as verifyResult, pia.verify_time as verifyTime, pia.verifier_name as verifierName, pia.generate_flag as generateFlag, pia.apply_name as applyName, pia.note from pension_infusion_apply pia where pia.cleared=2
and pia.older_id=#{olderId,jdbcType=INTEGER}
and pia.verify_flag=#{verifyFlag,jdbcType=VARCHAR}
and pia.verify_result=#{verifyResult,jdbcType=VARCHAR}
and pia.id=#{applyId,jdbcType=VARCHAR}
and unix_timestamp(pia.start_time) >=unix_timestamp(#{startDate})
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp(pia.start_time)
order by pia.generate_flag desc,pia.start_time desc

select pia.id, pia.older_id as olderId, pia.older_name as olderName, pia.older_age as olderAge, pia.treatment_scheme as treatmentScheme, pia.start_time as startTime, pia.end_time as endTime, pia.last_time as lastTime, pia.count_numbers as countNumbers, pia.execute_plan as executePlan, pia.execute_numbers as executeNumbers, pia.family_sign as familySign from pension_infusion_apply pia where pia.cleared=2
and pia.older_id=#{olderId,jdbcType=INTEGER}
and pia.generate_flag=1
