mapper.select:
select pd.eventName, pl.id, pr.visitTime, pl.checker_id as checkerId, pl.checkResult as checkresult, pl.checkTime as checktime, pl.handleResult as handleresult, pl.handler_id as handlerId, pl.handleTime as handletime, bed.`name` as bedName, floor.`name` as floorName, build.`name` as buildName, room.`name` as roomName , po.`name` as olderName, po.id as olderId from living_notice_record lnr,pension_livingnotify pl,pension_dic_event pd, pension_older po, pension_livingrecord pr, pension_bed bed, pension_room room, pension_floor floor, pension_building build where pl.record_id=lnr.id and pd.id=pl.event_id and po.id=lnr.older_id and pr.older_id=lnr.older_id and bed.id=pr.bed_id and bed.room_id=room.id and room.floor_id=floor.id and floor.build_id=build.id and lnr.dept_id=#{deptId}
and lnr.older_id=#{olderId}
and pr.visitTime >= #{startDate,jdbcType=TIMESTAMP}
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp(pr.visitTime)
and pl.handleResult= #{handleFlag,jdbcType=INTEGER}

select pd.eventName,pl.id, pl.checker_id as checkerId, pl.checkResult as checkresult,pl.checkTime as checktime, pl.handleResult,pl.handler_id as handlerId,pl.handleTime,dept.`name` as deptName, bed.`name` as bedName, floor.`name` as floorName, build.`name` as buildName, room.`name` as roomName , po.`name` as olderName, pl.checkTime as checktime, pl.check_flag as checkFlag from living_notice_record lnr,pension_livingnotify pl,pension_dic_event pd,pension_employee pe,pension_dept dept, pension_older po, pension_livingrecord pr, pension_bed bed, pension_room room, pension_floor floor, pension_building build where pl.record_id=lnr.id and pd.id=pl.event_id and pl.handleResult=1 and pe.id=pl.handler_id and pe.dept_id=dept.id and po.id=lnr.older_id and pr.older_id=lnr.older_id and bed.id=pr.bed_id and bed.room_id=room.id and room.floor_id=floor.id and floor.build_id=build.id
and lnr.older_id=#{olderId}
and pl.handleTime >= #{startDate,jdbcType=TIMESTAMP}
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp(pl.handleTime)
and pl.check_flag= #{checkFlag,jdbcType=INTEGER}
