mapper.select:
select paa.id, paa.older_id as olderId, paa.older_name as olderName, paa.service_item as serviceItem, paa.debit_amount as debitAmount, paa.return_amount as returnAmount, paa.apply_time as applyTime, paa.service_time as serviceTime, paa.expect_servetime as expectServetime, paa.actual_servetime as actualServetime, paa.verify_result as verifyResult, paa.verify_flag as verifyFlag, paa.agent_id as agentId, paa.agent_name as agentName, paa.finish_flag as finishFlag, paa.note as note, paa.evaluation, paa.send_flag as sendFlag from pension_agent_apply paa where paa.cleared=2 and paa.verify_flag=#{isAudit,jdbcType=VARCHAR}
and paa.verify_result=#{auditResult,jdbcType=VARCHAR}
and paa.finish_flag=#{isComplete,jdbcType=VARCHAR}
and paa.older_id=#{olderId,jdbcType=INTEGER}
and paa.id=#{applyId,jdbcType=INTEGER}
and paa.apply_time >= #{startDate,jdbcType=TIMESTAMP}
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp( paa.apply_time)
order by paa.apply_time desc

select paa.id, paa.older_id as olderId, paa.older_name as olderName, paa.service_item as serviceItem, paa.debit_amount as debitAmount, paa.apply_time as applyTime, paa.service_time as serviceTime, paa.expect_servetime as expectServetime, paa.verify_result as verifyResult, paa.verify_flag as verifyFlag from pension_agent_apply paa where paa.cleared=2
and paa.verify_flag=#{isAudit,jdbcType=VARCHAR}
and paa.verify_result=#{auditResult,jdbcType=VARCHAR}
and paa.older_id=#{olderId,jdbcType=INTEGER}
and paa.id=#{applyId,jdbcType=INTEGER}
and paa.apply_time >= #{startDate,jdbcType=TIMESTAMP}
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp( paa.apply_time)
order by paa.service_time

select paa.id, paa.older_id as olderId, paa.older_name as olderName, paa.service_item as serviceItem, paa.debit_amount as debitAmount, paa.return_amount as returnAmount, paa.apply_time as applyTime, paa.service_time as serviceTime, paa.expect_servetime as expectServetime, paa.actual_servetime as actualServetime, paa.agent_id as agentId, paa.agent_name as agentName, paa.note as note, paa.finish_flag as finishFlag, paa.pay_flag as payFlag, paa.payer_id as payerId, paa.payer_name as payerName from pension_agent_apply paa where paa.cleared=2 and paa.agent_id=#{userId,jdbcType=INTEGER}
and paa.older_id=#{olderId,jdbcType=INTEGER}
and paa.id=#{applyId,jdbcType=INTEGER}
and paa.service_time >= #{startDate,jdbcType=TIMESTAMP}
and unix_timestamp(date_add(#{endDate}, interval 1 day)) >=unix_timestamp( paa.service_time)
