mapper.select:
select pr.id, pr.purchase_no as purchaseNo, pr.approve_flag as approveFlag, pr.generate_time as generateTime, pr.purchaser_name as purchaserName, pr.purchase_time as purchaseTime, pr.total_money as totalMoney, pr.approve_flag as approveFlag, pr.send_flag as sendFlag, pr.approve_result as approveResult from pension_purchase_record pr where pr.cleared=2
and pr.purchase_time >=#{startDate,jdbcType=TIMESTAMP}
and pr.purchase_time <date_add(#{endDate}, interval 1 day)
and pr.send_flag=#{isSend,jdbcType=VARCHAR}
and pr.approve_result=#{auditResult,jdbcType=VARCHAR}

select pr.id, pr.purchase_no as purchaseNo, pr.approve_flag as approveFlag, pr.generate_time as generateTime, pr.purchaser_name as purchaserName, pr.purchase_time as purchaseTime, pr.total_money as totalMoney, pe.evaluator_name as evaluatorName, pr.approve_result as approveResult, pd.name as deptName from pension_purchase_record pr, pension_purchase_evaluate pe, pension_dept pd where pr.cleared=2 and pr.send_flag=1 and pe.apply_id=pr.id and pe.dept_id=pd.id and pe.dept_id=#{deptId,jdbcType=INTEGER}
and pr.purchase_no=#{purchaseNo,jdbcType=VARCHAR}
and pr.id=#{applyId,jdbcType=INTEGER}
